<script>
  import gsap from "gsap";
  import TextPlugin from "gsap/dist/TextPlugin";
  import ScrollToPlugin from "gsap/dist/ScrollToPlugin";
  import Observer from "gsap/dist/Observer";
  import { onMount } from "svelte";
  import { ScrollTrigger } from "gsap/dist/ScrollTrigger";
  import Project from "./Project.svelte";
  import Weather from "../../lib/assets/weather.svg";
  import Assignment from "../../lib/assets/assignment.svg";
  import Blog from "../../lib/assets/blog.svg";
  import Currency from "../../lib/assets/currency.svg";
  import News from "../../lib/assets/news.svg";
  import Random from "../../lib/assets/random.svg";
  import gCom from "../../lib/assets/gift-django.svg";
  import shop from "../../lib/assets/shop.svg";
  import stock from "../../lib/assets/stock.svg";

  onMount(() => {
    gsap.registerPlugin(TextPlugin, ScrollToPlugin, Observer, ScrollTrigger);

    const paths = document.querySelectorAll(".paths-work");
    paths.forEach((path, index) => {
      gsap.fromTo(
        path,
        {
          "stroke-dasharray": path.getTotalLength(),
          "stroke-dashoffset": path.getTotalLength(),
        },
        {
          "stroke-dashoffset": 0,
          duration: 1 + index * 2,
          ease: "power1.inOut",
          opacity: 1,
        }
      );
    });

    const btn = [
      {
        id: "btnOne",
        classPrefix: "prjOne",
        pathSelector: ".classOne",
      },
      {
        id: "btnTwo",
        classPrefix: "prjTwo",
        pathSelector: ".classTwo",
      },
      {
        id: "btnThree",
        classPrefix: "prjThree",
        pathSelector: ".classThree",
      },
      {
        id: "btnFour",
        classPrefix: "prjFour",
        pathSelector: ".classFour",
      },
      {
        id: "btnFive",
        classPrefix: "prjFive",
        pathSelector: ".classFive",
      },
      {
        id: "btnSix",
        classPrefix: "prjSix",
        pathSelector: ".classSix",
      },
      {
        id: "btnSeven",
        classPrefix: "prjSeven",
        pathSelector: ".classSeven",
      },
      {
        id: "btnEight",
        classPrefix: "prjEight",
        pathSelector: ".classEight",
      },
      {
        id: "btnNine",
        classPrefix: "prjNine",
        pathSelector: ".classNine",
      },
    ];

    btn.forEach((button) => {
      const btn = document.getElementById(button.id);
      btn.addEventListener("mouseenter", () =>
        handleMouseEnter(button.classPrefix, button.pathSelector)
      );
      btn.addEventListener("mouseleave", () =>
        handleMouseLeave(button.pathSelector)
      );
    });

    function handleMouseEnter(classPrefix, pathSelector) {
      gsap.to(`.${classPrefix}`, { opacity: 1 });
      const svgEl = document.querySelectorAll(pathSelector);
      opaqueArrow(svgEl);
    }

    function handleMouseLeave(pathSelector) {
      const svgEl = document.querySelectorAll(pathSelector);
      disopaqueArrow(svgEl, true);
    }

    function opaqueArrow(svgEl) {
      svgEl.forEach((path, index) => {
        gsap.fromTo(
          path,
          {
            "stroke-dasharray": path.getTotalLength(),
            "stroke-dashoffset": path.getTotalLength(),
          },
          {
            "stroke-dashoffset": 0,
            duration: 0.5 + index * 0.1,
            ease: "power1.inOut",
            opacity: 1,
          }
        );
      });
    }
    function disopaqueArrow(svgEl) {
      svgEl.forEach((path, index) => {
        gsap.to(path, {
          "stroke-dashoffset": path.getTotalLength(),
          duration: 0.5,
          ease: "power1.inOut",
          opacity: 0,
        });
      });
    }
  });

  function scrollTo() {
    const newY = document.querySelector("#section-next").offsetTop - 100;
    gsap.to(window, {
      duration: 0.7,
      scrollTo: {
        y: newY,
      },
      ease: "power1.inOut",
    });
  }
</script>

